.player--one--sp__container {
  position: absolute;
  z-index: 999;
  left: 0;
  bottom: 125px;
  width: 100%;
  display: flex;
  justify-content: space-around;
  align-items: center;
}

.p1--left__btn,
.p1--right__btn {
  stroke: chartreuse;
}

.player--two--sp__container {
  position: absolute;
  z-index: 999;
  left: 0;
  top: 125px;
  width: 100%;
  display: flex;
  justify-content: space-around;
  align-items: center;
}

.p2--left__btn,
.p2--right__btn {
  stroke: chartreuse;
}

