/* smartphone screen css */
/* ================= */
.smartphone--info__screen {
  position: absolute;
  top: 50%;
  transform: translate3d(-50%, -50%, 0);
  left: 50%;
  width: 80%;
  height: 80%;
  z-index: 10;
  /* background-color: red; */
  display: flex;
  flex-direction: column;
}

.player--1__screen {
  width: 100%;
  height: 50%;
  /* background-color: aqua; */
  transform-origin: center;
  transform: rotate(180deg);
}

.player--1__name {
  text-align: center;
  margin: 16px 0;
}

.player__nickname {
  display: block;
  text-align: center;
}

.player--1__screen span,
.player--2__screen span {
  display: block;
  margin-top: 6px;
  text-align: center;
}

.player--2__screen {
  width: 100%;
  height: 50%;
  /* background-color: green; */
  /* transform-origin: center; */
  /* transform: rotate(180deg); */
}

.player--1__screen,
.player--2__screen {
  opacity: 0.8;
  color: wheat;
}

.p1--left__btn,
.p1--right__btn,
.p2--left__btn,
.p2--right__btn {
  opacity: 0.6;
}

/* ===================== */
/* Query */
/* ===================== */
@media only screen and (min-width: 640px) {
  .player--one--sp__container,
  .player--two--sp__container {
    display: none;
  }
}

